update yeyak set yeyak_processing=#{yeyak_Processing}
, yeyak_start = sysdate, yeyak_end = sysdate+(INTERVAL '3' day) 		
where yeyak_num = #{yeyak_Num}

update yeyak set yeyak_processing=#{yeyak_Processing}
, yeyak_start = null, yeyak_end = null		
where yeyak_num = #{yeyak_Num}

update yeyak set yeyak_processing=#{yeyak_Processing}
, yeyak_end = sysdate		
where yeyak_num = #{yeyak_Num}
