mapper.select:
select b.* from( select rownum rn , a.* from ( select * from ( SELECT ROW_NUMBER() OVER(PARTITION BY b1.book_isbn ORDER BY b1.book_num DESC ) AS RNUM, b1.* , b2.all_book_cnt , NVL(b3.can_book_cnt,0) as can_book_cnt FROM book b1 join ( select book_isbn , COUNT(book_isbn) as all_book_cnt from book GROUP BY book_isbn ) b2 on b1.book_isbn = b2.book_isbn full join ( select book_isbn , count(book_isbn) as can_book_cnt from book where book_byn = 'Y' group by book_isbn ) b3 on b1.book_isbn = b3.book_isbn where book_title like '%' || #{ book_Title } || '%' and book_aut like '%' || #{ book_Aut } || '%' and book_pub like '%' || #{ book_Pub } || '%' )bk where RNUM = 1 order by
book_title asc book_aut asc book_location asc book_subject asc book_num asc
) a )b where rn between #{firstCnt} and #{lastCnt}

select b.* ,return_duedate , yeyak_processing  from book b
full join (
		   select * from history
		   where history.RETURN_DUEDATE in (
											select max(return_duedate) from HISTORY
											where book_num in (
															   select book_num from book
															   where book_isbn = #{book_Isbn}
															   )
											group by book_num
											)

		   ) h
on b.book_num = h.book_num
full join (
		   select * from yeyak
		   where yeyak.YEYAK_SUBMIT in(
									   select max(YEYAK_SUBMIT) from yeyak
									   where book_num in (
														  select book_num from book
														  where book_isbn = #{book_Isbn}
														  )
									   group by book_num
									   )
		   )y
on b.book_num = y.book_num
where book_isbn = #{book_Isbn}

select b.* from( select rownum rn , a.* from ( select * from ( SELECT ROW_NUMBER() OVER(PARTITION BY b1.book_isbn ORDER BY b1.book_num DESC ) AS RNUM, b1.* , b2.all_book_cnt , NVL(b3.can_book_cnt,0) as can_book_cnt FROM book b1 join ( select book_isbn , COUNT(book_isbn) as all_book_cnt from book GROUP BY book_isbn ) b2 on b1.book_isbn = b2.book_isbn full join ( select book_isbn , count(book_isbn) as can_book_cnt from book where book_byn = 'Y' group by book_isbn ) b3 on b1.book_isbn = b3.book_isbn where book_indate > sysdate - 30 )bk where RNUM = 1 order by
book_indate asc
) a )b where rn between #{firstCnt} and #{lastCnt}

select * from( select book.* , ROW_NUMBER() OVER(PARTITION BY book_isbn ORDER BY book_num asc ) AS RNUM from book where book_num in ( select book_num from( select h.book_num , count(*) as cnt from HISTORY h join BOOK b on h.book_num = b.book_num where b.book_subject = #{book_Subject} group by h.book_num order by cnt desc )
where ROWNUM < 11
) ) where rnum = 1 order by book_num asc
